tool.command:
if [ "$mesh_input_file" != "None" ] && [ ! -e '${$mesh_input_file.element_identifier}' ]; then ln -s '$mesh_input_file' '${$mesh_input_file.element_identifier}'; fi;
if [ "$geo_file" != "None" ] && [ ! -e '${$geo_file.element_identifier}' ]; then ln -s '$geo_file' '${$geo_file.element_identifier}'; fi;
if [ "$gmsh_path" != "None" ] && [ ! -e '${$gmsh_path.element_identifier}' ]; then ln -s '$gmsh_path' '${$gmsh_path.element_identifier}'; fi;
computeSurfaceNodeIDsInPolygonalRegion
    --mesh-input-file '$mesh_input_file.element_identifier'
    --geo-file '$geo_file.element_identifier'
#if str($gmsh_path).strip() != 'None' and str($gmsh_path).strip() != '':
    --gmsh-path '$gmsh_path.element_identifier'
#end if
